mapper.select:
select t1.*, (select t2.name from sys_menu t2 where t2.id = t1.pid) as parentName
from sys_menu t1
where t1.id = #{id}

select t1.id, t1.pid, t1.name, t1.url, t1.permissions, t1.menu_type, t1.icon, t1.sort, t1.creator, t1.create_date, t1.updater, t1.update_date from sys_menu t1
t1.menu_type = #{menuType}
order by t1.sort asc

select t3.id, t3.pid, t3.name, t3.url, t3.permissions, t3.menu_type, t3.icon, t3.sort, t3.creator, t3.create_date, t3.updater, t3.update_date from sys_role_user t1 left join sys_role_menu t2 on t1.role_id = t2.role_id left join sys_menu t3 on t2.menu_id = t3.id where 1=1 and t1.user_id = #{userId}
and t3.menu_type = #{menuType}
order by t3.sort asc
